Alias.script:
local roomID = getRoomIDbyHash(gmcp.room.info.system ..".".. gmcp.room.info.area.."."..gmcp.room.info.num)
setRoomChar(roomID, matches[2])
echo("symbol set\n")

local roomID = getRoomIDbyHash(gmcp.room.info.system ..".".. gmcp.room.info.area.."."..gmcp.room.info.num)
setRoomChar(roomID, "")
echo("symbol cleared\n")

local convertExit = { ["north"] = "n", ["northeast"] = "ne", ["east"] = "e", ["southeast"] = "se", ["south"] = "s", ["southwest"] = "sw", ["west"] = "w", ["northwest"] = "nw" }
local exitCommand = { ["n"] = true, ["ne"] = true, ["e"] = true, ["se"] = true, ["s"] = true, ["sw"] = true, ["w"] = true, ["nw"] = true } --the only valid direction names the setDoor function accepts
local doorStatus = { ["none"] = 0, ["green"] = 1, ["yellow"] = 2, ["red"] = 3 }
local roomID = getPlayerRoom() local dir = matches[2]
if convertExit[dir] ~= nil then --input is in the conversion table dir = convertExit[matches[2]] --go and convert it end
if doorStatus[matches[3]] == nil then --this is not a valid door color echo("door type must be 'none', 'green', 'yellow', or 'red'\n") elseif exitCommand[dir] then --we've got a valid direction input setDoor(roomID, dir, doorStatus[matches[3]]) echo(dir.." door status set to "..matches[3].."\n") else echo("please select a compass direction\n") end

setRoomCoordinates(getPlayerRoom(),matches[2],matches[3],0)
centerview(getPlayerRoom())
echo("room moved\n")

local areaID = getRoomArea(getPlayerRoom())
deleteArea(areaID)
echo("local area deleted\n")

local areaID = getRoomArea(getPlayerRoom())
if matches[2] == "on" then
  setGridMode(areaID, true)
  echo("map mode set to grid\n")
elseif matches[2] == "off" then
  setGridMode(areaID, false)
  echo("map mode set to default\n")
else
  echo("grid mode must be set to 'on' or off'\n")
end
